        SUBROUTINE BRETHC       THIS DOES GRAPHICAL OUTPUT OF TOTAL VENT. (DVENT)C       RESPIRATORY RATE (RRATE), ARTERIAL PO2, AND ARTERIALC       PCO2. ALKL ARE REFERRED TO THE COMMON ARRAY T.        CHARACTER DOT(1),BLANK(1),C(1),O(1),F(1),V(1),X(1),XLINE(65)        COMMON KT,KL,INI,NW1,NW2,JKL,IFLAG,NEOF,NW        COMMON NFLAG,J3,ISPAR,NA,NB,NC,ND,NE(8)        COMMON NARTI,MT,K2,K4,INDEX        COMMON T1(47),RRATE,T2(2),DVENT,T3(20),RO2PR,T4,RC2PR     /,T5(8),BUBBL        DATA DOT,BLANK,C,O,F,V,X /'.',' ','C','O','F','V','*'/        IFUNC(Z)=INT(Z*.5+1.1)        KD=IFUNC(RO2PR)        KK=IFUNC(RC2PR)        KR=IFUNC(RRATE)        KH=IFUNC(DVENT)        KN=IFUNC(BUBBL)-1        DO 150 K=1,65100     XLINE(K)=BLANK(1)        XLINE(1)=DOT(1)        IF (KD.LT.1.OR.KD.GT.65) GO TO 110105     XLINE(KD)=O(1)110     IF (KK.LT.1.OR.KK.GT.65) GO TO 120115     XLINE(KK)=C(1)120     IF (KR.LT.1.OR.KR.GT.65) GO TO 130125     XLINE(KR)=F(1)130     IF (KH.LT.1.OR.KH.GT.65) GO TO 135135     XLINE(KH)=V(1)140     IF (KN.LT.1.OR.KN.GT.65) GO TO 150145     XLINE(KN)=X(1)150     CONTINUEC       ***ALTERNATE VERSION NEXT THREE LINESC       MAX=MAX0(KD,KH,KK,KR,KN)C       IF(MAX.GT.65) MAX=65C       WRITE (*,160) J3,ND,(XLINE(I),I=1,MAX)        WRITE (*,160) J3,ND,XLINE160     FORMAT(' ',I3,'.',I2,65A1)        RETURN        END