        SUBROUTINE CHECK(I,NLOW,NHIGH)        DIMENSION LIMIT (30,2)        COMMON KT,KL,INI,NW1,NW2,JKL,NPRT,LDISP,KA,IFLAG,NEOF,NW        DATA LIMIT /5*0,-200,10,6*0,25,0,20,5,2*0,20,-10,-5,1,0,0,5*0,     /2*100,2*16000,90,2*16000,4000,90,4*16000,44,15,2000,5,25,80,10000,     /300,20,15,16000,16000,1,50,5,10,50/        IF (I.LT.31) GO TO 20        IF (NW2.LT.20) WRITE (*,10) I10      FORMAT ('*** This is pointless - factor ',I4,' will be ')        WRITE (*,11)11      FORMAT('changed back again on the next run ***')        NLOW=-100C       AS ABOVE, INTEGER SHOULD BE MAX ALLOWED BY LOCAL COMPUTERC       AND MAY BE HIGHER THAN 16000        NHIGH=16000        RETURN20      NLOW=LIMIT(I,1)        NHIGH=LIMIT(I,2)        RETURN        END