     	SUBROUTINE CLIN1(SIMLT)		DIMENSION ITRIG(72)        COMMON KT,KL,INI,NW1,NW2,JKL,IFLAG,NEOF,NW        COMMON NFLAG,J3,ISPAR,NA,NB,NC,ND,NE(8)        COMMON NARTI,MT,K2,K4,INDEX        COMMON FIO2,FIC2,CO,PD,FADM,BULLA,VLUNG,ELAST,VADM,AZ        COMMON BZ,CZ,BARPR,TEMP,TRQ,TC2MT,TVOL,HB,PCV,VBLVL        COMMON ADDC3,BC3AJ,DPG,PR,FITNS,SPACE,COMAX,SHUNT,VC,PEEP        COMMON VO2CT,PA,RPH,VPH,FVENT,BPH,AO2CT,AC2CT,AO2MT,AC2MT        COMMON AO2PR,AC2PR,DPH,XLACT,BO2PR,BC2PR,TIDVL,RRATE,RO2CT,VC2MT        COMMON DVENT,SVENT,PC2CT,PO2CT,TO2CT,TC2CT,BO2CT,BC2CT,TPH,RC3CT        COMMON VC2CT,RO2MT,RC2MT,XRESP,AN2MT,BO2MT,BC2MT,CBF,PC,DSPAC        COMMON REFLV,RO2PR,CONSO,RC2PR,PG,PJ,TND,RC2CT,QB,PW        COMMON FT,CONOM,BUBBL,PK,TC3MT,VC3MT,TC3CT,VC3CT,TLAMT,RLACT        COMMON BC3CT,BO2AD,COADJ,EO2CT,TO2MT,TO2PR,TC2PR,VO2MT,AVENT,PL        COMMON EC2CT,TN2MT,TN2PR,FEV,SN2PR,EN2CT,UN2MT,RN2MT,X109,X110        COMMON TC3AJ,SN2MT,QA,RVADM,XDSPA,BAG,XMALE,HT,WT,AGE        IF (NW2.GT.19) GO TO 110        WRITE (*,100)100     FORMAT(' Do you want 1. Preset patients OR 2. Specify your     /	own patients?')        NW2=0110     NA=18        FT=.16667        NARTI=1        ISPAR=1        NEOF=1        CALL NXTWD(7,XXX,NNN,1,2)        GO TO (120,280),NNN120     IF (NW2.GT.19) GO TO 140        WRITE(*,130)130     FORMAT(' The following preset patients are available:',/,     /	' 1. Normal fit subject exercising at 300 kcal per minute',/,     /	' 2. Same, at 900 kcal per minute',/,     /	' 3. UNFIT NORMAL SUBJECT EXERCISING AT 900 KPM/MIN',/,     /	' 4. NORMAL SUBJECT AT 10 ATMOSPHERES FOR 25 MINUTES',/,     /	' 5. CHRONIC AIRWAY OBSTRUCTION WITH VENTILATORY FAILURE',/,     /	' 6. SAME, WITH ACUTE EXACERBATION (I.E. PNEUMONIA)',/,     /	' 7. CHEYNE STOKES RESPIRATION DUE TO BRAIN STEM DAMAGE AND',/,     /	'    HEART DISEASE',/,     /	'    ........TYPE NUMBER OF YOUR CHOICE')        FT=.16667        NW2=0140     CALL NXTWD(7,XXX,NNN,1,7)        GO TO (150,160,170,190,200,240,250), NNNC       PRESET NUMBER ONE150     PD=400.        TRQ=.88        GO TO 180C       PRESET NUMBER TWO160     PD=800.        TRQ=.98        GO TO 180C       PRESET NUMBER THREE170     FITNS=35.5        GO TO 160180     NA=36        GO TO 255C       PRESET NUMBER FOUR190     BARPR=7600.*SIMLT        NA=30        SN2MT=2750.        J3=20        GO TO 230C       PRESET NUMBER FIVE200     VADM=28.        VC=1.2        VLUNG=5000.210     PR=30.        TC3MT=480.        BC3AJ=4.        TC2MT=15.7        VC2MT=2600.        BC2MT=1000.        ELAST=34.220     NA=72230     ISPAR=2        GO TO 260C       PRESET NUMBER SIX240     VC=1.        BULLA=30.        VADM=60.        XDSPA=30.        AZ=50.        VLUNG=7000.        GO TO 210C       PRESET SEVEN250     CZ=0.        AZ=160.        BZ=50.        CO=58.        VADM=30.        XRESP=0.        AC2MT=90.255     FT=.0833334260     NB=2        IF (NW2.GT.19) RETURN        WRITE(*,270)270     FORMAT(' THE SCALE IS ALTERED FOR THIS SIMULATION -      / 	WAIT WHILE I WORK THIS OUT.')        RETURN280     WRITE(*,290)290     FORMAT(' THIS FACILITY NOT PRESENTLY AVAILABLE.')300     RETURN        END