      		SUBROUTINE COMMT(I,XXX)        COMMON KT,N(28),FIO2,FIC2,T(15),HB        GOTO (10,40,70,80,100,120,140,210,210,210,210,210,210,210,210,     /160,160,180,210,160,210,210,210,210,210,210,210,210,200,210), I10      IF (XXX.GE.10.) GOTO 220        WRITE (*,20)20      FORMAT (' YOUR BABY ISN''T GOING TO LIKE THIS VERY MUCH')        WRITE (*,30)30      FORMAT (' UNLESS BAROMETRIC PRESSURE IS ABOVE NORMAL.')        GOTO 22040      IF (XXX.LT.21.) GOTO 50        WRITE (*,60)        GOTO 23050      IF (XXX.LT.9.) GOTO 230        WRITE (*,20)60      FORMAT (' THIS WILL BE HORRIBLE')        GOTO 23070      IF (XXX.LT.10.) WRITE (*,60)        GOTO 21080      IF (XXX.GT.2800.) WRITE (*,90)90      FORMAT (' THIS IS ABOVE THE PROBABLE MAXIMUM ENERGY USED')        WRITE (*,91)91      FORMAT(' BY A SPRINTING RUNNER.')        GOTO 210100     IF (XXX.GT.60.) WRITE (*,110)110     FORMAT(' THAT''S A BIG SHUNT, EVEN FOR FALLOT''S ')        GOTO 210120     IF (XXX.LT.3500.) GOTO 130        WRITE (*,60)        GOTO 210130     IF (XXX.LT.2500.) GOTO 210        WRITE (*,20)        GOTO 210140     IF ((XXX.LT.1000.).OR.(XXX.GT.12000.)) WRITE (*,150)150     FORMAT (' THAT''S A BIT RIDICULOUS')        GOTO 210160     IF (XXX.LT.6.) WRITE (*,170)170     FORMAT(' THAT''S REALLY UNPHYSIOLOGICALLY LOW')        GOTO 210180     IF (XXX.LT.HB) WRITE (*,190)        IF (XXX.LT.HB) WRITE (*,191)190     FORMAT(' A FALL OF HEMOGLOBIN WITHOUT CHANGE IN O2 CONTENT')191     FORMAT(' WILL RAISE PO2 BRIEFLY. NOT A SURPRISE IF YOU THINK!')        GOTO 210200     IF (XXX.LT..7) WRITE (*,170)210     RETURN220     IF (XXX+FIC2-100.03) 210,210,240230     IF (XXX+FIO2-100.) 210,210,240240     WRITE (*,250)250     FORMAT(' IMPOSSIBLE GAS MIXTURE - THINK AGAIN')        RETURN		END