        SUBROUTINE CONST(C,NREPT,SIMLT)C       THIS S/R COMPUTES ALL PARAMETERS CONSTANT DURING A RUN        DIMENSION C(70)        COMMON KT,KL,N(27)        COMMON FIO2,FIC2,CO,PD,FADM,BULLA,VLUNG,ELAST,VADM,AZ,     /BZ,CZ,BARPR,TEMP,TRQ,TC2MT,TVOL,HB,PCV,VBLVL,     /ADDC3,BC3AJ,DPG,PR,FFITNS,SPACE,COMAX,SHUNT,VC,PEEP,     /VO2CT,PA,T(10),DPH,T2(3),TIDVL,RRATE,T3(24),CONSO,T4(7),FT,     /CONOM,T5(16),AVENT,PL,T6(3),FEV,T7(4),T8(4),QA,RVADM,     /XDSPA,T9(2),HT,WT10      IF (N(13).GT.1.OR.N(12).EQ.1) GO TO 111        WRITE (*,100)100     FORMAT(' (KPA)(0)',12X,'(4)',12X,'(8)',11X,'(12)',11X,'(16)',/,     /' MINS 0',8X,'20',8X,'40',8X,'60',8X,'80',8X,'100',7X,'120')        WRITE (*,110)110     FORMAT(' +SECS .',12(4X,'.'))111     X=TIDVL*RRATEC       FIRST BRACKETED PARTS OF NEXT 2 STATEMENTS PREVENT AUTOMATIC REDUCTIONC       OF ITERATION INTERVAL IF MAXIMUM CARDIAC OUTPUT IS TOO LOW - THUSC       ALLOWING AN OPERATOR TO FIX CARDIAC OUTPUT BY INCREASING CO ANDC       REDUCING COMAX        IF((CO.GT.340..OR.PD.GT.950..OR.SHUNT.GT..5).AND.FT.GT..04)     /GO TO 112        IF((CO.GT.210..OR.PD.GT.350..OR.FIO2.LT.7..OR.CO.LT.5..OR.     /PL.LT.0..OR.X.LT.1..OR.PR.LT.5.).AND.FT.GT..09)GO TO114        GOTO 120112     FT=.0333334        GOTO 116114     FT=.0833334116     WRITE(*,118)118     FORMAT(' ITERATION INTERVAL TOO LONG FOR THIS SITUATION. I HAVE      /SHORTENED IT.',/,' TO CHANGE BACK, USE 4. RUN CHANGE OPTION')120     IF ((N(23).EQ.0.AND.X.LT.1.).OR.PL.LT.-.5.OR.PR.LT.1.) AVENT=.001        IF (TEMP.GE.30.) GO TO 140        WRITE(*,130)130     FORMAT(' SORRY - NO CAN DO - LET''S SAY 30 DEGREES C')        TEMP=30.140     C(1)=1000./VBLVL        C(2)=100./VBLVL        C(3)=.0203*HB        C(4)=(ELAST+105.)*.01        C(5)=2.7/(VC+.4)        C(6)=FEV*25.+29.        C(7)=CONSO*PD*.00081*(TEMP-26.)**1.05        C(8)=(30.-PEEP*5./ELAST)*.0016*CONOM*(TEMP-12.2)        C(9)=(C(7)-CONSO)*.01        C(10)=FT*.005        C(11)=BARPR/SIMLT-1.2703*TEMP        C(12)=C(11)*.003592/(273.+TEMP)        C(13)=.9/TVOL        C(14)=SHUNT+1.        C(15)=2./WT        C(16)=CO*.01        C(17)=FT*10.        C(18)=VADM*80.        C(19)=(PD-90.)*RVADM*.05        IF (C(19).LT.-1.) C(19)=-1.        C(20)=650.*VC        IF (BULLA.GT.0.) C(20)=C(20)+SQRT(BULLA)*15.        C(21)=(40.-PEEP)*.025        C(22)=4.5/FT        C(23)=20.*SIMLT/BARPR        C(24)=CONOM*.3        C(25)=100.*C(12)        C(26)=7./TVOL        C(27)=FT*.1        C(28)=30000./(VBLVL+1000.)        C(29)=FT*.0039*WT**.425*HT**.725        C(30)=520./TVOL        C(31)=2.7/TVOL        C(32)=C(29)+.0000001        C(33)=C(3)*308.-TVOL*.65*C(30)        C(34)=.004/(C(12)*FT)        C(35)=.01/C(12)        C(36)=7.7*C(13)        C(37)=SPACE/FT        C(38)=20./VLUNG        C(39)=FT*.127        C(40)=C(29)*(PD-25.)*1.3        C(41)=FT*(TEMP-24.5)*1.82        C(42)=.003*C(29)        C(43)=1./(1.+7.7*C(3))        X=(PD*.01)**.8*VC*.2        C(44)=AZ*X*.0132        C(45)=BZ*X*.008        C(46)=CZ*.78*((C(7)*.00051)**.97+.01)        X=.5+356./C(11)        IF (X.GT.1.) X=1.        C(47)=PR*.000214*(TEMP-29.)**1.5*X        C(48)=.04*(TEMP-26.)*VC        C(49)=9.+SQRT(ELAST*1.25)        C(50)=(150.+PR)*.0275*(TEMP-17.)        C(51)=RRATE*TIDVL*.001        C(52)=VLUNG*.03-20.+BULLA        C(53)=.8/FT        C(54)=XDSPA*.001        C(55)=.1/FT        C(56)=.001/FT        C(57)=FT*60.        C(58)=FT*1.27        C(59)=FT*.3        C(60)=FT*.008        C(61)=.22/FT        C(62)=FT*240000./(CZ+300.)        C(63)=FT*C(7)*.12        C(64)=.01488*HB*(TVOL+VBLVL*.001)        C(65)=7.324-CZ*.00005        C(66)=C(65)-.002        C(67)=CZ-30.        C(68)=FT*3000./(PD+200.)        C(69)=(FITNS-20.)*.00035        C(70)=C(29)*1.3        ADDC3=ADDC3/FLOAT(NREPT)        DPH=7.4+(DPG-3.8)*.025        IF (DPH.GT.7.58) DPH=7.58        IF (DPG.GT.13.) DPG=13.        RETURN        END