C		This routine has been reworked to allow survival to a worse C		physiological condition than the original version.C		J. Michael Dean 11/29/94C        SUBROUTINE DEATH(LL1,LL5)C       THIS ROUTINE CHECKS FOR ERRORS AND/OR DEATH OF SUBJECT        DIMENSION MM(6)        COMMON KT,KL,NTAB(27),T(95)        EQUIVALENCE (PD,T(4)),(TPH,T(59)),(PG,T(75)),(BUBBL,T(83))        DATA MM /60,87,88,95,41,42/        IF (LL5) 10,50,50C       ERRORS DIAGNOSISC10      DO 20 I=1,6        LL5=MM(I)        IF(T(LL5)-.001) 30,20,2020      CONTINUE        LL5=030      WRITE(*,40) LL540      FORMAT(' ** Factor ',I2,' has gone out of bounds.')		WRITE(*,*) 'The time interval is probably too long.'        LL1=3        GO TO 19050      LL1=1        IF (PG) 60,70,7060      PG=0.70      IF (PG-7.) 80,80,10080      IF (TPH-6.63) 120,120,9090      IF (TPH-7.8) 190,190,14095      IF (BUBBL-280.) 300,300,155100     WRITE(*,*) 'Anoxia will be severe and irrecoverable.'        GOTO 160120     WRITE(*,*) 'Tissue pH has fallen below 6.63 - very bad.'        GO TO 160140     WRITE(*,*) 'Tissue pH has risen above 7.8 - this is bad.'        GO TO 160155     WRITE(*,*) 'The brain is full of nitrogen gas bubbles.'160     WRITE(*,*) 'Your patient should be considered dead.'		WRITE(*,*) 'Even in an intensive care unit setting.'180     LL1=2190     RETURN300     IF (PD-290.) 360,310,310310     IF (TPH-7.1) 320,320,360320     WRITE(*,*) ' ...I ........cannot.....go............on.....'360     RETURN        END