        SUBROUTINE DELAY        COMMON KT,KL,INI,NW1,NW2,JKL,IFLAG,NEOF,NW        COMMON NFLAG,J3,ISPAR,NA,NB,NC,ND,NE(8)        COMMON NARTI,MT,K2,K4,INDEX        COMMON T1(30),VO2CT,T2(29),VC2CT,T3(19),FT,T4(4),VC3MT        COMMON T5(6),COADJ,T6(3),TC2PR,T7(23),TDLAY(40)C       DELAY LINE FOR CIRCULATION OF GSES, ETC.        NFT=IFIX(13.2*SQRT(COADJ*FT))        IF (NFT-10) 110,110,100100     NFT=10110     M=INDEX+NFT-1        DO 140, I=INDEX,M        N=I        IF(N-10) 130,130,120120     N=N-10130     TDLAY(N)=VO2CT        TDLAY(N+10)=VC2CT        TDLAY(N+20)=VC3MT140     TDLAY(N+30)=TC2PR        N=INDEX+NFT        IF(N-10) 160,160,150150     N=N-10160     VO2CT=TDLAY(N)        VC2CT=TDLAY(N+10)        VC3MT=TDLAY(N+20)        TC2PR=TDLAY(N+30)        INDEX=N        RETURN        END