        SUBROUTINE DUMP(LL1)        COMMON KT,KL,INI,NW1,NW2,JKL,IFLAG,NEOF,NW        COMMON NTAB(20),T(160),NDUMP(20),TDUMP(160)        IF (LL1.GT.9) GO TO 140        IF (NTAB(5).EQ.2) GO TO 120        IF (LL1.GT.1) GO TO 170        IF (NW2.GT.19) GO TO 110        WRITE(*,100)100     FORMAT(' DO YOU WANT TO 1.STORE PRESENT STATE, 2.BACKTRACK     / TO LAST STORED STATE.')110     CALL NXTWD(14,XXX,NNN,1,2)        GO TO (120,170), NNN120     WRITE(*,130)130     FORMAT(' STORED AT THIS POINT.')140     DO 150, I=2,20150     NDUMP(I)=NTAB(I)        DO 160, I=1,160160     TDUMP(I)=T(I)        RETURN170     WRITE(*,180)180     FORMAT(' BACKTRACKED TO LAST STORED STATE.')        DO 190, I=2,20190     NTAB(I)=NDUMP(I)        DO 200, I=1,160200     T(I)=TDUMP(I)        LL1=1        RETURN        END