	    SUBROUTINE MINIT(LL1,LL3,LL4,NREPT,SIMLT)C       MCAID IS INTERFACE FLAG FOR MCAID DRIVER        DIMENSION REFER(120)        COMMON KT,KL,INI,NW1,NW2,JKL,IFLAG,NEOF,NW        COMMON NFLAG,J3,ISPAR,NA,NB,NC,ND,NE(8)        COMMON NARTI,MT,K2,K4,INDEX        COMMON T(120),TDLAY(40)C       CONSTANTS FOR INITIALIZING THE ARRAY OF VARIABLESC       FOR A NEW SUBJECT        DATA REFER /20.93,.03,2*100.,0.,0.,3000.,5.,3.,100.,     /2*100.,760.,37.,.8,13.38,12.,14.8,45.,3000.,     /0.,0.,3.8,100.,33.,.4,35.,0.,5.,0.,     /14.56,1.,7.40,7.37,4.26,7.33,2*0.,348.1,143.3,     /101.9,39.75,7.4,0.,28.89,52.8,461.9,12.82,19.54,1540.,     /2*5.92,47.25,19.66,14.47,51.4,10.05,56.62,7.37,23.82,     /51.33,195.44,473.5,10.99,1987.,18.06,677.,51.94,.80,129.2,     /3000.,94.18,240.,39.9,0.,97.13,-10.,47.35,33.29,2.36,     /.166667,4.6,0.,45.43,318.,71.55,2*25.48,34.63,.99,     /22.7,1.,5.01,19.54,178.4,40.13,45.43,437.,710.8,0.,     /47.35,76.07,571.1,4.,564.,.73,0.,7.26,1.,1.,     /-.03,967.,41.61,3*0.,1.,178.,70.,40./10      WRITE(*,20)20      FORMAT(' ----          MacPuf - New Simulation        ----')30      IF (LL1.LT.10) GO TO 200        T(81)=.16666740      CALL QUERY (LL3)        NW2=050      CALL NXTWD(7,XXX,LL3,0,2)        IF (LL3) 120,150,120120     IF (LL3-2) 140,130,140130     IFLAG=-5140     CALL QUERY (LL3)        GO TO 160150     NC=3160     IF(NW2.GT.19) GO TO 170        WRITE(*,102)102		FORMAT('To use S.I. units type 1; for mm Hg type 2')        NW2=0170     CALL NXTWD(7,XXX,NNN,1,2)        SIMLT=1.        IF (NNN.EQ.1) SIMLT=.1332200     NW=1        NARTI=1        J3=0        MT=1        K2=0        K4=0        IF (NC-3) 250,220,250220     IF (LL1-10) 260,230,260230     CALL QUERY (18)260     WRITE(*,270) NE270     FORMAT(10H Mins     ,7(1H(,I3,1H),3X),1H(,I3,1H))250     INDEX=1        NREPT=1        X=T(81)        DO 320 I=1,120320     T(I)=REFER(I)        DO 330 I=1,10        TDLAY(I)=T(31)        TDLAY(I+10)=T(61)        TDLAY(I+20)=T(86)330     TDLAY(I+30)=T(97)        T(13)=T(13)*SIMLT        IF(LL1.LT.10) GO TO 240        CALL DUMP(LL1)        LL1=1240     T(81)=X        T(77)=-X*60.        LL4=-1        RETURN        END