        SUBROUTINE SYMPT(SIMLT)        COMMON KT,KL,INI,NW1,NW2,JKL,IFLAG,NEOF,NW        COMMON NFLAG,J3,ISPAR,NA,NB,NC,ND,NE(8)        COMMON NARTI,MT,K2,K4,INDEX        COMMON T1(12),BARPR,T2(4),HB,T3(26),BO2PR,BC2PR,T4,RRATE,     /RO2CT,T5,DVENT,SVENT,T6(6),TPH,T7(15),PG,T8(5),FT,CONOM,BUBBL,     /T9(19),TN2PRC       K1-K4 CONCERNED WITH IMPROVEMENT OF SYMNPTOMS        X=1./SIMLT100     K1=0        K3=0        IF (TN2PR*X-6000.) 110,110,130110     IF (12.5-BO2PR*X) 120,120,130120     IF (BC2PR*X-91.) 150,150,130130     WRITE(*,140)140     FORMAT(' YOUR PATIENT IS UNAROUSEABLE.')        K1=1        GOTO 428150     IF (BO2PR*X-13.9) 160,160,180160     WRITE(*,170)170     FORMAT('      MY EYES ARE GOING DIM...')        K1=1180     IF (BUBBL-160.) 210,210,190190     WRITE(*,200)200     FORMAT('  I HAVE AWFUL PAINS IN MY LEGS!')        K1=1        GOTO 240210     IF (BUBBL-101.) 240,240,220220     WRITE(*,230)230     FORMAT('   MY SKIN IS ITCHING.')        K3=1240     IF (TN2PR*X-4300.) 250,250,260250     IF (BC2PR*X-80.) 280,280,260260     WRITE(*,270)270     FORMAT(' I AM FEELING DROWSY.')        K3=1280     IF (NARTI) 390,390,290290     IF (RRATE-46.) 330,330,300300     WRITE(*,310)310     FORMAT('   THIS...... IS .......IMPOSSIB...........')320     K1=1        GOTO 390330     IF (RRATE-35.) 360,360,340340     WRITE(*,350)350     FORMAT('   I AM VERY SHORT OF BREATH.')        GOTO 320360     IF (RRATE-27.) 390,390,370370     WRITE(*,380)380     FORMAT('  I AM RATHER SHORT OF BREATH.')        K3=1390     IF (TPH-7.59) 420,420,400400     WRITE(*,410)410     FORMAT(' I AM GETTING TINGLING AND CRAMPS IN MY HANDS.')        K3=1420     IF (TPH-7.13) 424,424,428424     WRITE(*,426)426     FORMAT('  I DON''T FEEL WELL AT ALL!!')        K3=1428     IF (TPH-7.08) 430,450,450430     WRITE(*,440)440     FORMAT(' YOUR PATIENT IS TWITCHING.')        K1=1450     IF (NARTI) 460,460,490460     X=SVENT*.9-DVENT        IF (X) 490,490,470470     WRITE(*,480)480     FORMAT('  YOUR PATIENT IS FIGHTING THE VENTILATOR.')        K3=1490     X=1.3*HB-RO2CT        IF (X-7.) 520,500,500500     WRITE(*,510)510     FORMAT(' YOUR PATIENT IS VERY BLUE.')        K1=1        GOTO 550520     IF (X-5.) 550,530,530530     WRITE(*,540)540     FORMAT(' YOUR PATIENT IS BLUE.')        K3=1550     IF (K1-K3) 570,570,560560     K3=1570     K5=K1+K3        IF (K2-K5) 600,600,580580     WRITE(*,590)590     FORMAT(' GOD BLESS YOU, DOCTOR. I FEEL REALLY WELL AGAIN.'     /,/,' IT''S LIKE A MIRACLE!')        GOTO 660600     IF (K2-K1) 630,630,610610     WRITE(*,620)620     FORMAT(' I FEEL BETTER, BUT NOT QUITE RIGHT.  CAN''T YOU     /DO SOMETHING ELSE FOR ME?')        GOTO 660630     IF (K4-K3) 660,660,640640     WRITE(*,650)650     FORMAT(' THAT''S BETTER.  ARE YOU GOING TO DO ANY OTHER     /NASTY THINGS TO ME?')660     K2=K1        K4=K3        IF (NB.NE.2) GO TO 670C       ALTER NUMBER OF ITERATIONS AFTER CLIN1 CALL IN STEADY STATE        NA=30        IF (FT.LT..1) NA=18        NB=0670     RETURN        END